mapper.select:
SELECT * FROM t_tfs_mainorg

    and orgcode = #{orgCode}
    and productid = #{productId}
    and usertype = #{userType}
    and certificatetype = #{certificatetype}
    and certificatenumber = #{certificateNumber}
    and buslince = #{buslince}

mapper.update:
update t_tfs_mainorg
orgname = #{orgName}, statusid = #{statusId}, certificatetype = #{certificatetype}, certificatenumber = #{certificateNumber}, connect = #{connect}, mobiletel = #{mobileTel}, buslince = #{buslince},
where orgcode = #{orgCode}
