mapper.select:
        select `id`,
        `orderno`,
        `creditorderno`,
        `orgcode`,
        `amount`,
        `actualamount`,
        `rate`,
        `rspid`,
        `rsorgid`,
        `producttype`,
        `productId`,
        `createtime`,
        `ratetmp`,
        `status`,
        `errormsg`,
        `relmoneytime`,
        `productspecId`,
        `repaymenttype`,
        `actualrepaymentdate`,
        `lastrepaymentdate`,
        `repaymentprincipal`,
        `repaymentinterest`,
        `shouldcapital`,
        `shouldinterest`
        from t_loan_order

        	1=1


                and orderno = #{orderNo}



                and producttype = #{productType}



            	and status in

			            #{item}   




		    	AND actualrepaymentdate >= #{beginActualRepaymentDate}


		    	AND actualrepaymentdate <= #{endActualRepaymentDate}



		    	AND createtime >= #{beginCreateTime}


		    	AND createtime <= #{endCreateTime}




		    	AND lastrepaymentdate >= #{beginLastRepaymentDate}


		    	AND lastrepaymentdate <= #{endLastRepaymentDate}



		    	AND relmoneytime >= #{beginRelMoneyTime}


		    	AND relmoneytime <= #{endRelMoneyTime}



                and orgcode = #{orgCode}

mapper.update:
update t_loan_order
amount= #{amount}, actualamount= #{actualAmount}, rate= #{rate}, rspid= #{rspId}, orderid= #{orderid}, rsorgid= #{rsorgId}, producttype= #{productType}, productid= #{productId}, createtime= #{createTime}, ratetmp=#{rateTmp}, status= #{status}, errormsg= #{errorMsg}, relmoneytime= #{relMoneyTime}, productspecId= #{productSpecId}, repaymenttype= #{repaymentType}, actualrepaymentdate= #{actualRepaymentDate}, lastrepaymentdate= #{lastRepaymentDate}, repaymentprincipal= #{repaymentPrincipal}, repaymentinterest= #{repaymentInterest}, shouldcapital= #{shouldCapital}, shouldinterest= #{shouldInterest},
orderno = #{orderNo}
