mapper.select:
select
*
from t_tfs_balanceinfo

    accountid = #{accountid}
     and finaccountid = #{finaccountid}
     and userid =#{userid}
     and productid =#{productid}

mapper.insert:
insert into t_tfs_balanceinfo (accountid, finaccountid, userid, 
  productid, accountcode, accountname, 
  currency, initcreditamount,initsettleamount,initfrozonamount,
  initoverlimit,initusablelimit,inittotalamount,status,creator,createtime,modifier)
values (#{orgid}, #{orgcode}, #{orgname}
   )
