mapper.select:
select 
*
from t_tfs_orgbindinfo

	 userid = #{userid}
	 and  merchantcode = #{merchantcode}
	 and  orgcode = #{orgcode}
	 and  bindstatus = #{bindstatus}

mapper.insert:
insert into t_tfs_orgbindinfo (userid, merchantcode, orgcode, 
  merchantname, bindstatus,  creator, 
  createtime, modifier, modifytime
  )
values (#{userid,jdbcType=VARCHAR}, #{merchantcode,jdbcType=VARCHAR}, #{orgcode,jdbcType=VARCHAR}, 
  #{merchantname,jdbcType=VARCHAR}, #{bindstatus,jdbcType=INTEGER},  #{creator,jdbcType=VARCHAR}, 
  #{createtime}, #{modifier,jdbcType=VARCHAR}, #{modifytime}
  )

mapper.select:
     select userid, merchantcode, orgcode, merchantname, bindstatus,  creator, 
      createtime, modifier, modifytime from t_tfs_orgbindinfo  
      
      	bindstatus = 1
	      
	        and userid=#{userid}
	      
	      
	        and orgcode=#{orgcode}
	      
	      
	        and merchantcode = #{merchantcode}

select obi.userid, obi.merchantcode, obi.orgcode, obi.merchantname, obi.bindstatus, obi.creator,obi.createtime, obi.modifier, obi.modifytime from t_tfs_orgbindinfo obi inner join t_tfs_org org on org.userid =obi.userid
org.statusid=1
and merchantcode = #{merchantcode}
