mapper.select:
select refundid,orderid,refundAmount,refundOrderno,orderTime,creator,status from t_tfs_refund 

  
  refundid = #{refundId}
  
  
    orderid =#{orderNo}
  
  
    refundOrderno =#{refundOrderno}
  
  
   status =#{status}

mapper.update:
update t_tfs_refund set status = #{status} 

   
     refundid = #{refundId}
   
   
     and orderid =#{orderNo}
   
   
     and refundOrderno =#{refundOrderno}
