mapper.select:
select refundid,orderid,refundAmount,refundOrderno,orderTime,creator,status,transferAmount,
       fee,userOrderId,payOrderNo,notifyUrl,businessInfo,createtime,'' as version, '' as statusid  from t_tfs_refund

  
  refundid = #{refundId}
  
  
    orderid =#{orderNo}
  
  
    refundOrderno =#{refundOrderno}
  
  
   status =#{status}
  
  
   userOrderId=#{userOrderId}

mapper.update:
update t_tfs_refund

	status = #{status},
	refundOrderno = #{refundOrderno},


   
     refundid = #{refundId}
   
   
     and orderid =#{orderNo}
