mapper.select:
select
*
from t_tfs_transorder

    userid=#{userid}
    transid =#{transid}
    orderid =#{orderid}
    businessordercode =#{businessordercode}
    payorderno =#{payorderno}

              select o.*  
                     from 
                     t_tfs_transorder o
                     
                       
		                 
		                    and payeemerchant = #{partner}
		                 
                         and transordertype=#{transordertype}
		                 and payermerchant =#{userid} 
		                 and statusid !=10 and statusid !=0 order by createtime desc

select o.* 
       from 
       t_tfs_transorder o
       
        
        
          and payermerchant = #{partner}
       
       and transordertype=#{transordertype}
       and payeemerchant =#{userid} 
       and statusid !=10 and statusid !=0
       order by createtime desc

select transid, constid, userid, ordertypeid, productid, orderid, userorderid, businessordercode, orderdate,
ordertime, provider, userrelateid,amount, goodsname, goodsdetail, goodscnt, unitprice, statusid,
adjusttype, adjustcontent, creator,receivablefee,standfee, createtime, receivedfee,escrowedDate,isEscrowedPayment,
payeemerchant,payermerchant,payorderno,merchantcode,tradeamount,transordertype,notifyUrl,businessinfo,payertype as payerType
 from t_tfs_transorder

    
        and transid = #{transid}
    
    
        and orderid = #{orderid}
    
    
        and userorderid = #{userorderid}
    
    
        and businessordercode = #{businessordercode}
    
    
        and userid = #{userid}
    
    
        and payorderno = #{payorderno}
    
    
        and payertype = #{payertype}
    

order by createtime desc

select transorder.transid, transorder.isEscrowedPayment, transorder.escrowedDate, transorder.orderid, transorder.tradeamount, transorder.payeemerchant, transorder.payermerchant, transorder.userrelateid, transorder.userorderid, transorder.productid, transorder.userid, transorder.constid, transorder.notifyUrl, transorder.payorderno, transorder.businessordercode, transorder.creator, transorder.receivedfee, transfer.transorderid, transfer.status from t_tfs_transorder transorder inner join t_tfs_orderpayreq orderpay on orderpay.transorderid = transorder.transid left join t_tfs_transferreq transfer on transorder.transid=transfer.transorderid
and transorder.statusid=#{status}
and transorder.payermerchant = #{payermerchant}
and orderpay.reqstatus=#{orderPayStatus}
