mapper.select:
select 
*
from t_tfs_user
where status!=0
	 and tfsuserid = #{tfsuserid}
	 and userid = #{userid}
	 and orgcode = #{orgcode}
	 and userloginname = #{userloginname}
	 and isadmin = #{isadmin}
	 and isoperator = #{isoperator}
	 and status = #{status}

    SELECT
	  u.tfsuserid
    FROM
	    t_tfs_user u
      LEFT JOIN t_tfs_userbindinfo b ON u.tfsuserid = b.tfsuserid 
    where status!=0
    	 and b.isactive = #{bindIsactive}
    	 and u.status = #{status}
    	 and u.status != #{excludeStatus}
		 and u.userid = #{userId}
		 and u.orgcode = #{orgCode}
         and u.tfsuserid = #{tfsUserId}
      	 and u.userloginname = #{userLoginName}
      	 and b.fcloginname = #{bindLoginName}
      	 and b.merchantcode = #{bindMerchantCode}
      	 and b.cooptype = #{coopType}
         and b.fcuserid = #{bindUserId}
      	 and u.username like concat('%',#{userName},'%')
      	 and u.mobilephone = #{mobilePhone}
    GROUP BY u.tfsuserid

select tfsuserid, userid, orgcode, username, userloginname, password, department, mobilephone, email, 
merchantcode, paypassword,paySalt, status, isadmin,creator, createtime, modifier, 
modifytime from t_tfs_user where tfsuserid =#{tfsuserid}
